import React, { useState } from 'react';
import './login.css'; // Import the CSS file containing your styles

function FlipCard() {
  const [isSignUp, setIsSignUp] = useState(false);

  const handleToggle = () => {
    setIsSignUp(!isSignUp);
  };

  const handleClick=(e)=>{
    e.preventDefault()
    const buy={email}
    console.log(buy)
    fetch("http://localhost:2024/login",{
      method:"POST",
      headers:{"Content-Type":"application/json"},
      body:JSON.stringify(buy)

  }).then(()=>{
    console.log("New buyer added")
  })
}

  return (
    <div className="wrapper">
      <label className="switch">
        <input className="toggle" type="checkbox" onChange={handleToggle} checked={isSignUp} />
        <span className="slider"></span>
        <span className="card-side"></span>
        <div className={`flip-card__inner ${isSignUp ? 'flipped' : ''}`}>
          <div className="flip-card__front">
            <div className="title">Log in</div>
            <form action="" className="flip-card__form">
              <input type="email" placeholder="Email" name="email" className="flip-card__input" />
              <input type="password" placeholder="Password" name="password" className="flip-card__input" />
              <button className="flip-card__btn">Let's go!</button>
            </form>
          </div>
          <div className="flip-card__back">
            <div className="title">Sign up</div>
            <form action="" className="flip-card__form">
              <input type="text" placeholder="Name" className="flip-card__input" />
              <input type="email" placeholder="Email" name="email" className="flip-card__input" />
              <input type="password" placeholder="Password" name="password" className="flip-card__input" />
              <button className="flip-card__btn">Confirm!</button>
            </form>
          </div>
        </div>
      </label>
    </div>
  );
}

export default FlipCard;
